CREATE TABLE education (
id SERIAL PRIMARY KEY,
sch_Name VARCHAR(255),
major VARCHAR(255),
ed_Level VARCHAR(255),
sch_Loc VARCHAR(255),
grad_Date VARCHAR(255),
sch_Logo TEXT,
user_id INT references users(id)
);

CREATE TABLE work (
id SERIAL PRIMARY KEY,
emp_Name VARCHAR(255),
position VARCHAR(255),
emp_Loc VARCHAR(255),
hire_Date VARCHAR(255),
end_Date VARCHAR(255),
emp_Logo TEXT,
user_id INT REFERENCES users(id)
);

CREATE TABLE skills (
id SERIAL PRIMARY KEY,
skill VARCHAR(255),
user_id INT
);

CREATE TABLE languages (
id SERIAL PRIMARY KEY,
language VARCHAR(255),
user_id INT
);

CREATE TABLE projects (
id SERIAL PRIMARY KEY,
project VARCHAR(255),
user_id INT
);

CREATE TABLE services (
id SERIAL PRIMARY KEY,
user_id INT REFERENCES users(id),
price  VARCHAR(10),
service VARCHAR(255),
image TEXT
);

